INSERT INTO article (id, slug, title, body, `view`, category_id, thumbnail_base_url, thumbnail_path, `status`, created_by, updated_by, published_at, created_at, updated_at) VALUES
(1, '3月22日，受天津市科委委托，天津科学技术评价中心组织召开天津市科技支撑计划项目“面向多种类型大数据实时监测与深度分析”结题验收会。', '“面向多种类型大数据的实时监测与深度分析”项目通过验收', '<p>3月22日，受天津市科委委托，天津科学技术评价中心组织召开天津市科技支撑计划项目“面向多种类型大数据实时监测与深度分析”结题验收会。会议由该中心有关负责人主持，五位评审专家出席，项目负责人袁晓洁教授、徐敬东教授以及有关教师和同学参加。</p><p>验收会现场</p><p>袁晓洁教授作项目工作总结报告</p><p>袁晓洁教授从研究目标、项目内容与研究过程、项目成果、经费与财务审计等四个方面对整体情况进行了概括和总结。项目子课题负责人张莹副教授、刘杰副教授、张玉副教授分别围绕面向行业领域的多源异构大数据信息融合与深度分析、面向Web大数据的实体检索与智能分析、大数据的实时处理与流数据分析等三个方面，结合同步演示和操作，讲解了各个子课题的具体技术理论与应用实现。在专家质询环节，五位评审专家与各位教师进行了交流</p><p><img src="http://cc.nankai.edu.cn/images_upload/%E5%A4%A7%E6%95%B0%E6%8D%AE%E9%A1%B9%E7%9B%AE%E9%AA%8C%E6%94%B6/1.PNG"><br></p>', '', 1, 'http://storage.yii2-starter-kit.dev/source', '1/J3zjnncafmJ-YFeNhcTeE51Z8JrqPiE4.png', 1, 1, 1, 1492069060, 1492069060, 1492586393),
(2, '3月2日，校党委书记魏大鹏来到计算机与控制工程学院进行调研。', '魏大鹏到计控学院调研', '<p><strong>南开新闻网讯</strong>(记者 聂际慈 摄影报道)3月2日，校党委书记魏大鹏来到计算机与控制工程学院进行调研。</p><p>　　学院负责人向魏大鹏介绍了学院历史沿革、组织架构、学科建设、人才培养、外事工作、科研成果、差距与奋斗目标等内容，并希望学校在体制机制设计、教师队伍分系列管理、学科协调发展等方面给予进一步支持。</p><p>　　魏大鹏详细听取了发言，他指出，计控学院是一个年轻的学院，办学过程中克服诸多困难，在人才培养、师资队伍建设、教学科研与服务社会等方面做了大量工作。南开自创校以来就有着工科发展的传统与基础，在当前学校“双一流”建设过程中，也将大力发展现代工科。希望大家进一步解放思想、转变观念、抓好落实。学校将做好顶层设计，学院要抢抓这一战略新机遇，扎实推进各项工作向前发展。</p><p>　　魏大鹏强调，学科发展关键在人才，学院要加强人才工作，在班子建设、人才培养、人才引进等方面下功夫，用南开的品牌与影响力吸引人、用先进高效的管理服务留住人。要发挥平台作用，让国家级虚拟仿真实验教学中心、天津市智能机器人技术重点实验室等平台产出更多高水平成果。要理顺体制机制，激励专业教师在“教学好”的同时，还要“研究好”。要做好调查研究，协调好计算机学科与软件学科的关系。</p><p><img src="http://news.nankai.edu.cn/pic/0/00/24/69/246984_159756.jpg"><br></p>', '', 1, 'http://storage.yii2-starter-kit.dev/source', '1/IWbEqBoJZFInR38K53f8t8WviZLCLe56.jpg', 1, 1, 5, 1492069132, 1492069132, 1495153374),
(3, 'slug', 'bigdata', '<h2>The view file does not exist: </h2>', '', 1, 'http://storage.yii2-starter-kit.dev/source', '1/uHuHDxV24W3lDcJRkdZqytiOl_onPMn-.png', 1, 1, 1, 1492496664, 1492496664, 1492496707),
(4, '法国南特中央理工大学校长来访我校并签署合作协议', '法国南特中央理工大学校长来访我校并签署合作协议', '<p><strong>南开新闻网讯</strong>(记者 乔仁铭 摄影 吴军辉)4月17日，法国南特中央理工大学校长阿诺·普渡一行访问我校，校长龚克、副校长严纯华在津南校区综合业务东楼会见了客人。</p><p>　　龚克对阿诺·普渡一行到访南开表示热烈欢迎并介绍了我校的基本概况、学科布局、津南校区运行、国际交流合作等情况。龚克表示，南开大学十分重视国际交流，积极鼓励学生拓展海外视野。南开一直与南特中央理工大学保持着良好的沟通与互动，希望未来双方继续加强在工程教育领域的合作，携手并进，共同发展，进一步加深两校师生间的了解与认识。</p><p>　　阿诺·普渡对南开大学近年来取得的发展成绩表示高度赞赏，并介绍了南特中央理工大学的发展现状、国际合作项目等情况。他说，南开大学是中国著名的高等学府，来到南开，非常高兴和激动。南特中央理工大学高度重视两校交流，希望未来双方能够在科学研究、学生联合培养等方面进行深层合作。</p><p>　　会谈结束后，龚克与阿诺·普渡分别代表双方签署了两校在计控、电光领域的联合培养硕士协议。</p><p>　　我校计控学院、电光学院及国际学术交流处负责人参加。</p><p>　　据了解，南特中央理工大学位于法国西部重要的港口城市南特，成立于1919 年，是法国最好的工程师院校之一，该校数学和机器人领域研究位于法国前列。2016 年5 月，我校与南特中央理工大学签署两校备忘录及学生交换协议，并在数学、物理、计控、环科、材料领域开展3+1+2 联合培养。2016 年，我校有11 位同学入选该联合培养项目，其中有两名同学获得欧盟奖学金支持。</p><p><br></p>', '', 2, 'http://storage.yii2-starter-kit.dev/source', '1/ztbZIV7IO19E8UmfqPtlde-3k7FDnPKi.jpg', 1, 1, 1, 1492587763, 1492587763, 1492587763),
(5, 'test', 'test', '<p>stes</p>', '', 1, NULL, NULL, 1, 1, 1, 1494332941, 1494332983, 1494332983);

